sources.styles/ui/scores.scss:
.score-card {
  display: flex;
  flex-direction: row;
  height: 85vh;
}

.score-card__head {
  padding-top: 70px;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  max-height: 230px;
  width: 100%;
}
.score-col {
  align-items: center;
  border-right: 1px solid $white;
  flex: 1;
  display: flex;
  flex-direction: column;
  height: 100%;
  &:last-child {
    border-right: none;
  }
}

.score-listing {
  position: relative;
  display: flex;
  flex-direction: column;
  height: auto;
  width: 100%;
  overflow: scroll;
}

.score-item {
  align-items: center;
  border-bottom: 1px solid white;
  box-sizing: border-box;
  display: flex;
  flex: 1;
  justify-content: center;
  max-height: 60px;
  min-height: 60px;
  width: 100%;
  text-align: center;
  margin-bottom: 8px;
  padding-left: 10px;

  @include respond-to(tablet-up) {
    max-height: 110px;
    min-height: 110px;
  }
}

sources.styles/breakpoints.scss:
/********************************************
        BREAKPOINTS
********************************************/

$phone-end: "600px";
$tablet-start: "601px";
$tablet-end: "1015px";
$desktop-start: "1016px";
$desktop-end: "1750px";
$widescreen-start: "1751px";
$widescreen-end: "2400px";
$widescreen-hd-start: "2401px";

// * Based on mobile first approach.

@mixin respond-to($media) {
  // ? Tablet > <
  @if $media == tablet-up {
    @media only screen and (min-width: $tablet-start) {
      @content;
    }
  }

  @if $media == tablet-down {
    @media only screen and (max-width: $phone-end) {
      @content;
    }
  }

  // ? Desktop > <
  @if $media == desktop-up {
    @media only screen and (min-width: $desktop-start) {
      @content;
    }
  }

  @if $media == desktop-down {
    @media only screen and (max-width: $tablet-end) {
      @content;
    }
  }

  // ? Widescreen > <
  @if $media == widescreen-up {
    @media only screen and (min-width: $widescreen-start) {
      @content;
    }
  }
  @if $media == widescreen-down {
    @media only screen and (max-width: $desktop-end) {
      @content;
    }
  }

  // ? Widescreen HD > <
  @if $media == widescreen-hd-up {
    @media only screen and (min-width: $widescreen-hd-start) {
      @content;
    }
  }
}

